mapper.select:
select

from t_coupon_record
where deleted = false
  AND coupon_id = #{couponId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

select

from t_coupon_record
        where deleted = false


        AND expired > #{now} AND status = #{status}


        AND expired < #{now}
                OR status = #{status}



    AND user_id = #{userId}

order by create_at desc
limit #{offset},#{limit}

select tcr.id, tcr.coupon_id, tcr.user_id, tcr.`status`, tcr.expired, tcr.create_at, tcr.update_at, tcr.deleted from t_coupon_record tcr JOIN t_coupon tc on tcr.coupon_id = tc.id where tcr.deleted = false AND tcr.expired > #{now} AND tcr.status = 0
AND tcr.user_id = #{userId}
order by tc.reduce_money desc limit #{offset},#{limit}

select

from t_coupon_record
        where deleted = false

    AND create_at > #{startTime}


    AND create_at < #{endTime}


    AND status = #{status}


    AND coupon_id = #{couponId}

order by create_at desc
limit #{offset},#{limit}

select count(id)
from t_coupon_record
        where deleted = false

    AND create_at > #{startTime}


    AND create_at < #{endTime}


    AND status = #{status}


    AND coupon_id = #{couponId}

select count(1)
from t_coupon_record
where coupon_id = #{couponId,jdbcType=BIGINT}
